package com.def.demo;

public class ForTest {

	public static void main(String[] args) {
		char[] ASD={'D','A','D'};
		// TODO Auto-generated method stub
		//Tapan kumar Rout nnn
		for(int i=0;i<4;i++ )
		{
			if(i==3)
			{
				System.out.println("MOMMY");
			}
			else
			{
				for(int j=0;j<=4; j++)
				{
					if(j==4)
					{
						System.out.println(ASD[i]);
					}
					else
					{
						System.out.print(" ");
					}
					
				}
				
			}
			
			
		}
		

	}

}
